import React, { useContext, useEffect, useRef, useState } from 'react'
import Noteitem from './Noteitem'
import { NoteContext } from '../context/notes/noteContext'
import AddNote from './AddNote';
import { useNavigate } from 'react-router-dom'

function Notes(props) {
    const context = useContext(NoteContext);
    let history = useNavigate();
    const { notes, getNotes, editNote } = context;
    useEffect(() => {
        if (localStorage.getItem('token')) {
            getNotes();
        } else {
            history("/login")
        }
    })
    const ref = useRef(null);
    const updateNote = (currentNote) => {
        ref.current.click()
        setNote({ etitle: currentNote.title, edescription: currentNote.description, etag: currentNote.tag })
        props.showAlert("Updated Successfully", "success");
        // props.showAlert("Invalid Detials","danger")

    }
    const [note, setNote] = useState({ etitle: "", edescription: "", etag: "" })
    const handleClick = (e) => {
        console.log("Updating tht note...", note)
        editNote(note.id, note.etitle, note.edescription, note.etag);
        refClose.current.click();
        props.showAlert("Updated Successfully", "success")
            // addNote(note.title, note.description, note.tag);
    }
    const onChange = (e) => {
            setNote({...note, [e.target.name]: e.target.value })
        }
        // const ref = useRef(null);
    const refClose = useRef(null)
    return ( <
        >
        <
        AddNote showAlert = { props.showAlert }
        / >

        <
        button type = "button"
        ref = { ref }
        className = "btn btn-primary"
        dataToggle = "modal"
        dataTarget = "#exampleModal" >
        Launch demo modal <
        /button>


        <
        div className = "modal fade"
        id = "exampleModal"
        tabIndex = "-1"
        ariaLabelledby = "exampleModalLabel"
        ariaHidden = "true" >
        <
        div className = "modal-dialog" >
        <
        div className = "modal-content" >
        <
        div className = "modal-header" >
        <
        h5 className = "modal-title"
        id = "exampleModalLabel" > Edit Note < /h5> <
        button type = "button"
        className = "btn-close"
        dataBsDismiss = "modal"
        ariaLabel = "Close" > < /button> < /
        div > <
        div className = "modal-body" >
        <
        form className = "container my-3" >
        <
        div className = "form-group" >
        <
        label htmlFor = "title" > Title < /label>  <
        input type = "text"
        className = "form-control"
        id = "etitle"
        name = "etitle"
        value = { note.etitle }
        ariaDescribedby = "emailHelp"
        onChange = { onChange }
        minLength = { 5 }
        required /
        >

        <
        /
        div > <
        div className = "form-group" >
        <
        label htmlFor = "description" > Description < /label>  <
        input type = "text"
        className = "form-control"
        id = "edescription"
        value = { note.edescription }
        name = "edescription"
        onChange = { onChange }
        minLength = { 3 }
        required /
        >
        <
        /
        div >

        <
        div className = "form-group" >
        <
        label htmlFor = "tag" > Tag < /label>  <
        input type = "text"
        className = "form-control"
        id = "etag"
        name = "etag"
        value = { note.etag }
        onChange = { onChange }
        minLength = { 3 }
        required /
        >
        <
        /
        div >
        <
        /
        form >
        <
        /div> <
        div className = "modal-footer" >
        <
        button ref = { refClose }
        type = "button"
        className = "btn btn-secondary"
        dataBsDismiss = "modal" > Close < /button> <
        button type = "button"
        className = "btn btn-primary"
        onClick = { handleClick }
        disabled = { note.etitle.length < 5 || note.edescription.length < 3 || note.etag.length < 3 } > Update Note < /button> < /
        div > <
        /div> < /
        div > <
        /div>

        <
        div classNameName = "container md-3" >
        <
        h1 > Your Notes < /h1>   <
        div classNameName = "row md-3 d-flex" > { notes.length === 0 && "No notes to display" }

        {
            notes.map((note) => {
                return <Noteitem note = { note }
                key = { note._id }
                updateNote = { updateNote }
                showAlert = { props.showAlert }
                /> ;
            })
        } <
        /div> < /
        div >
        <
        />

    )
}

export default Notes
